mapper.select:
SELECT member_id 
FROM chat_room_member 
WHERE room_id = #{roomId}

SELECT EXISTS (
    SELECT 1 
    FROM chat_room_member 
    WHERE room_id = #{roomId} 
    AND member_id = #{userId}
)

mapper.update:
UPDATE chat_room_member 
SET last_read_at = now() 
WHERE room_id = #{roomId} AND member_id = #{memberId}
